pragma solidity ^0.4.23;

import "./Ownable.sol";

contract LottoFactory is Ownable {

  event NewLotto(uint lottoId, string name, uint timestamp); 

  struct Lotto {
    string name;
    uint timestamp;
  }

  Lotto[] public lottos;

  mapping (uint => address) public lottoOwner;
  mapping (address => uint) public lottoCount; 

  function createLotto(string _name) public {
    uint id = lottos.push(Lotto(_name, now));
    lottoOwner[id] = msg.sender;
    lottoCount[msg.sender]++;
    emit NewLotto(id, _name, lottos[id].timestamp); 
  }

}
