pragma solidity ^0.4.23;

import "./LottoFactory.sol";

contract LottoGame is LottoFactory {

  event NewLottoGame(uint lottoId, string name, uint timestamp, uint endTime); 

  mapping (uint => address) public lottoGames;

  function playLottoGame(uint _lottoId, uint _duration) public {
    uint timestamp = now;
    uint endTimestamp = timestamp + _duration;
    Lotto memory lotto = lottos[_lottoId];
    emit NewLottoGame(_lottoId, lotto.name, lotto.timestamp, endTimestamp); 
  }

}
